import React, { Component } from "react";
import {
  Button,
  Form,
  Grid,
  Header,
  Dropdown,
  Message,
  Segment,
  Container
} from "semantic-ui-react";
import Head from "next/head";

class LoginForm extends Component {
  state = {
    departments: [],
    roles: []
  };

  async componentDidMount() {
    try {
      const dptResponse = await axios.get("/api/departments");
      this.setState({ departments: dptResponse.data.departments });
      const rolesResponse = await axios.get("/api/roles");
      this.setState({ roles: rolesResponse.data.roles });
    } catch (error) {
      console.error(error);
    }
  }

  render() {
    return (
      <Container>
        <Head>
          <link
            rel="stylesheet"
            href="//cdn.jsdelivr.net/npm/semantic-ui@2.4.2/dist/semantic.min.css"
          />
        </Head>
        <Grid
          textAlign="center"
          style={{ height: "100vh" }}
          verticalAlign="middle"
        >
          <Grid.Column style={{ maxWidth: 450 }}>
            <Header as="h2" color="teal" textAlign="center">
              Signup to the Idea Portal
            </Header>
            <Form size="large">
              <Segment stacked>
                <Form.Input
                  fluid
                  icon="user"
                  iconPosition="left"
                  placeholder="E-mail address"
                />
                <Form.Input
                  fluid
                  icon="address card"
                  iconPosition="left"
                  placeholder="E-mail address"
                />
                <Form.Input
                  fluid
                  icon="lock"
                  iconPosition="left"
                  placeholder="Password"
                  type="password"
                />
                <Form.Input
                  fluid
                  icon="lock"
                  iconPosition="left"
                  placeholder="Repeat Password"
                  type="password"
                />

                <Form.Dropdown
                  placeholder="Select Friend"
                  fluid
                  selection
                  options={friendOptions}
                />

                <Button color="teal" fluid size="large">
                  Login
                </Button>
              </Segment>
            </Form>
            <Message>
              Already Registred? <a href="/">Login</a>
            </Message>
          </Grid.Column>
        </Grid>
      </Container>
    );
  }
}

export default LoginForm;
