import React, { Component } from "react";
import {
  Button,
  Form,
  Grid,
  Header,
  Message,
  Segment,
  Container
} from "semantic-ui-react";
import Head from "next/head";

class LoginForm extends Component {
  state = {
    email: "",
    password: ""
  };

  handleEmailChange = event => {
    this.setState({ email: event.target.value });
  };

  handlePasswordChange = event => {
    this.setState({ password: event.target.value });
  };

  loginHandler = async => {
    try {
      console.log(this.state.email, this.state.password);
      // const config = {
      //   headers: {
      //     "Content-Type": "application/json"
      //   }
      // };
      // const obj = { email: this.state.email, password: this.state.password };
      // const res = await axios.post("/api/auth/login/", obj, config);
      // localStorage.setItem("token", res.data.token);
      // console.log( "token:" + res.data.token);
    } catch (err) {
      window.alert("Login Failed Wrong User/Password Combination");
    }
  };

  render() {
    return (
      <Container>
        <Head>
          <link
            rel="stylesheet"
            href="//cdn.jsdelivr.net/npm/semantic-ui@2.4.2/dist/semantic.min.css"
          />
        </Head>
        <Grid
          textAlign="center"
          style={{ height: "100vh" }}
          verticalAlign="middle"
        >
          <Grid.Column style={{ maxWidth: 450 }}>
            <Header as="h2" color="teal" textAlign="center">
              Log-in to your account
            </Header>
            <Form size="large" onSubmit={this.loginHandler}>
              <Segment stacked>
                <Form.Input
                  required
                  fluid
                  icon="user"
                  iconPosition="left"
                  placeholder="E-mail address"
                  type="email"
                  onChange={this.handleEmailChange}
                  value={this.state.email}
                />
                <Form.Input
                  required
                  fluid
                  icon="lock"
                  iconPosition="left"
                  placeholder="Password"
                  type="password"
                  onChange={this.handlePasswordChange}
                  value={this.state.password}
                />

                <Button color="teal" fluid size="large">
                  Login
                </Button>
              </Segment>
            </Form>
            <Message>
              New to us? <a href="/signup">Sign Up</a>
            </Message>
          </Grid.Column>
        </Grid>
      </Container>
    );
  }
}

export default LoginForm;
