import React, { Component } from "react";
import Layout from "../components/Layout";
import { Card } from "semantic-ui-react";
import { Link } from "../routes";
import axios from "axios";
import { Pagination } from "semantic-ui-react";

class ElectionIndex extends Component {
  state = {
    selectedPage: Number,
    numberOfPages: Number,
    listOfIdeas: []
  };

  // async componentDidMount() {
  //   this.setState({ selectedPage: 1 });
  //   try {
  //     const res = await axios.get(
  //       `http://localhost:5000/api/ideas?itemsCount=5&pageNo=${this.state.selectedPage}`
  //     );
  //     this.setState({ listOfIdeas: res.data.ideas });
  //     this.setState({ numberOfPages: Math.ceil(res.data["totalIdeas"] / 5) });
  //   } catch (err) {
  //     console.log(err);
  //   }
  // }

  static async getInitialProps(props) {
    try {
      const res = await axios.get(
        `http://localhost:5000/api/ideas?itemsCount=5&pageNo=${this.state.selectedPage}`
      );
      let ideas = res.data.ideas;
      let numberOfPages = Math.ceil(res.data["totalIdeas"] / 5);
    } catch (err) {
      console.log(err);
    }
    return { ideas, numberOfPages };
  }

  renderIdeas() {
    const items = this.state.listOfIdeas.map((idea, index) => {
      return {
        key: index,
        header: idea.Title,
        description: idea.description,
        fluid: true
      };
    });
    return <Card.Group items={items} />;
  }

  render() {
    return (
      <Layout>
        <div>
          <h3>Dashboard Homepage</h3>
          {this.renderIdeas()}
          <br></br>
          <Pagination
            boundaryRange={0}
            defaultActivePage={1}
            ellipsisItem={null}
            firstItem={null}
            lastItem={null}
            siblingRange={1}
            totalPages={2}
          />
        </div>
      </Layout>
    );
  }
}

export default ElectionIndex;
