import React, { Component } from "react";
import Layout from "../components/Layout";
import { Link } from "../routes";

class ElectionIndex extends Component {
  state = {
    selectedPage: Number
  };

  async componentDidMount() {
    this.setState({ selectedPage: 1 });
    try {
      const res = await axios.get(
        `/api/ideas?itemsCount=5&pageNo=${this.state.selectedPage}`
      );
      this.setState({ listOfIdeas: res.data.ideas });
      this.setState({ totalIdeas: res.data["totalIdeas"] });
      console.log(res.data);
    } catch (err) {
      console.log(err);
    }
  }

  renderIdeas() {
    const items = this.props.elections.map(address => {
      return {
        header: address,
        description: (
          <Link route={`/elections/${address}`}>
            <a>View Election</a>
          </Link>
        ),
        fluid: true
      };
    });
    return <Card.Group items={items} />;
  }

  render() {
    return (
      <Layout>
        <div>
          <h3>Dashboard Homepage</h3>
        </div>
      </Layout>
    );
  }
}

export default ElectionIndex;
