import React, { Component } from "react";
import { Form, Button, Input, Message } from "semantic-ui-react";

class ElectionNew extends Component {
  state = {
    username: "",
    password: "",
    errorMsg: "",
    loading: false
  };

  static async getInitialProps(props) {}

  onSubmit = async event => {
    event.preventDefault(); //keeps the browser from attempting to submit the form
  };
  render() {
    return (
      <Layout>
        <h3>Login</h3>
        <Form onSubmit={this.onSubmit} error={!!this.state.errorMsg}>
          <Form.Field>
            <label>Type your credentials</label>
            <Input
              value={this.state.description}
              onChange={event =>
                this.setState({ description: event.target.value })
              }
            />
          </Form.Field>
          <Message error header="Error!" content={this.state.errorMsg} />
          <Button loading={this.state.loading} primary>
            Login
          </Button>
        </Form>
      </Layout>
    );
  }
}
export default ElectionNew;
