import React, { Component } from "react";
import {
  Button,
  Form,
  Grid,
  Header,
  Message,
  Segment,
  Container
} from "semantic-ui-react";
import Head from "next/head";
import axios from "axios";

class LoginForm extends Component {
  state = {
    departments: [],
    roles: []
  };

  async componentDidMount() {
    try {
      const dptResponse = await axios.get(
        "http://localhost:5000/api/departments"
      );
      this.setState({ departments: dptResponse.data.departments });
      const rolesResponse = await axios.get("http://localhost:5000/api/roles");
      this.setState({ roles: rolesResponse.data.roles });
      console.log(dptResponse.data);
    } catch (error) {
      console.error(error);
    }
  }

  render() {
    const dptOptions = this.state.departments.map((dpt, index) => ({
      key: dpt.department,
      text: dpt.department,
      value: dpt.department
    }));

    const rolesOptions = this.state.roles.map((role, index) => ({
      key: role.roles,
      text: role.roles,
      value: role.roles
    }));
    return (
      <Container>
        <Head>
          <link
            rel="stylesheet"
            href="//cdn.jsdelivr.net/npm/semantic-ui@2.4.2/dist/semantic.min.css"
          />
        </Head>
        <Grid
          textAlign="center"
          style={{ height: "100vh" }}
          verticalAlign="middle"
        >
          <Grid.Column style={{ maxWidth: 450 }}>
            <Header as="h2" color="teal" textAlign="center">
              Signup to the Idea Portal
            </Header>
            <Form size="large">
              <Segment stacked>
                <Form.Input
                  fluid
                  icon="user"
                  iconPosition="left"
                  placeholder="E-mail address"
                />
                <Form.Input
                  fluid
                  icon="address card"
                  iconPosition="left"
                  placeholder="E-mail address"
                />
                <Form.Input
                  fluid
                  icon="lock"
                  iconPosition="left"
                  placeholder="Password"
                  type="password"
                />
                <Form.Input
                  fluid
                  icon="lock"
                  iconPosition="left"
                  placeholder="Repeat Password"
                  type="password"
                />

                <Form.Dropdown
                  placeholder="Select Department"
                  fluid
                  selection
                  options={dptOptions}
                />

                <Form.Dropdown
                  placeholder="Select Role"
                  fluid
                  selection
                  options={this.state.roles}
                />

                <Button color="teal" fluid size="large">
                  Login
                </Button>
              </Segment>
            </Form>
            <Message>
              Already Registred? <a href="/">Login</a>
            </Message>
          </Grid.Column>
        </Grid>
      </Container>
    );
  }
}

export default LoginForm;
